advanced.customcss:
.main-slider .section-title.large .heading { font-size: 7rem; text-transform: uppercase; font-weight: 700; line-height: 1.1; margin-bottom: 25px; margin-top: 80px; }

.main-slider .section-title.large .excerpt { font-size: 2rem; color: #fff; }

.main-slider .button { margin-top: 65px; }

.main-slider .swiper-slide .overlay { padding-bottom: 120px; }

.section-features { padding: 120px 0 50px }

.section-features .media .heading { margin: 18px 0; font-size: 1.4rem; text-transform: uppercase; }

.section-features .media .heading + .excerpt { font-size: 1.35rem; padding: 0 3%; }


.section-features .column { margin-bottom: 76px; }

.wrapper-site .section-portfolio .media { overflow: hidden; display: block; }

.wrapper-site .section-portfolio .media-image  { border-top: #fff 1px solid; border-left: #fff 1px solid;  }

.wrapper-site .section-portfolio .media .media-image img { transition: all .5s ease; }

.wrapper-site .section-portfolio .media:hover .media-image img { transform: scale(1.5) rotateZ(25deg) }

.wrapper-site .media .heading + .excerpt { margin-top: 0; }

.wrapper-site .section-portfolio .media-body { position: absolute; top: 0; background: rgba(232,69,69,.8); height: 100%; margin:0; transition: all .8s ease; opacity: 0; -webkit-transform: translateZ(0); }

.wrapper-site .section-portfolio .media:hover .media-body { opacity: 1; }
 
.section-portfolio .media .heading { margin-top: 25%; text-transform: uppercase; }

.section-portfolio .button { background: none; border: #fff 1px solid; padding: 5px 20px; text-transform: uppercase; }


.section-portfolio .button:hover { background: #fff; color: #E84545; }

.section-team .media .heading { font-family: Raleway; margin-top: 6px; }

.section-team .media .heading + .excerpt { font-size: 1.4rem; }

.section-team .media .heading + .excerpt strong { text-transform: uppercase; color: #E84545; font-size: 1.3rem; font-weight: normal; }

.section-team .column .media-image { overflow: hidden; }

.section-team .column .media-image img { transition: all .5s ease; }

.section-team .column:hover .media-image img { transform: scale(1.1) }

.section-test .container { padding: 0; }

.section-test .column.span-6 { min-height: 480px; margin-bottom: 0; padding: 7% 5%; }

.section-test .media .heading { font-family: Cardo; font-style: italic; font-size: 3.2rem; margin-bottom: 20px; max-width: 500px; }

.section-test .media .heading + .excerpt { text-transform: uppercase; font-weight: bold; font-size: 1.3rem; }

.section-download .section-title .heading { font-family: Raleway; font-weight: 300; }

.section-download .section-title .excerpt { text-transform: uppercase; font-size: 1.25rem; font-weight: bold; margin-bottom: 20px; }

.section-download .button { padding: 12px 70px; }
